import maya.cmds as cmds
import maya.mel as mel

root_name = self.get_root_name()
ns = root_name.split("picker")[0]

def setState(part, item):
    global ns
    if cmds.getAttr(ns+part+"_control.fkIk"):
        item.text.set_text("IK")
        item.set_color(QtGui.QColor(170, 135, 135, 255))
    else:
        item.text.set_text("FK")
        item.set_color(QtGui.QColor(100, 130, 150, 255))

# cycle all items
views = self.views[root_name]
for view in views:
    for item in view.items:
        if item.name == "add_icon":
            v = cmds.getAttr(ns+'character.offsetControls')
            if v: item.polygon.opacity = 255
            else: item.polygon.opacity = 100
        elif item.name == "cloth_icon":
            v = cmds.getAttr(ns+'character.clothControls')
            if v: item.polygon.opacity = 255
            else: item.polygon.opacity = 100
        elif item.name == "face_icon":
            v = cmds.getAttr(ns+'character.FaceControls')
            if v: item.polygon.opacity = 255
            else: item.polygon.opacity = 100
        elif item.name == "face_face_icon":
            v = cmds.getAttr(ns+'character.FaceControls')
            if v: item.polygon.opacity = 255
            else: item.polygon.opacity = 100
        elif item.name == "ikFk_arm":
            setState("l_arm", item)
        elif item.name == "ikFk_arm_MIRROR":
            setState("r_arm", item)
        elif item.name == "ikFk_leg":
            setState("l_leg", item)
        elif item.name == "ikFk_leg_MIRROR":
            setState("r_leg", item)
